import { observer } from "mobx-react";
import { ScrollView, View } from "native-base";
import React, { useState } from "react";
import { Button, Center, HStack } from "native-base";

// Components
import RequestItem from "./RequestItem";
import SearchBar from "../Search/SearchBar";
// Stores
import RequestStore from "../../stores/requestStore";

const RequestsList = ({ navigation }) => {
  const [query, setQuery] = useState("");

  const [isUrgent, setisUrgent] = useState(false);

  const [donated, setDonated] = useState(false);

  const requestList = RequestStore.requests
    .filter((request) =>
      request.bloodType.toLowerCase().includes(query.toLowerCase())
    )
    .map((request) => (
      <RequestItem
        request={request}
        key={request._id}
        navigation={navigation}
      />
    ));

  const filteredUrgent = RequestStore.requests
    .filter((request) => request.priority === "URGENT")
    .map((request) => (
      <RequestItem
        request={request}
        key={request._id}
        navigation={navigation}
      />
    ));

  const filterDonated = RequestStore.requests
    .filter((request) => request.donate === true)
    .map((request) => (
      <RequestItem
        request={request}
        key={request._id}
        navigation={navigation}
      />
    ));

  return (
    <View>
      <Button onPress={() => setisUrgent(!isUrgent)}>
        {isUrgent ? "Normal" : "Urgent"}
      </Button>
      <Button onPress={() => setDonated(!donated)}>
        {donated ? "All" : "Donated"}
      </Button>
      <HStack space={3} alignItems="center">
        <ScrollView>
          <Text
            h="7"
            w="10"
            bg="white"
            rounded="md"
            shadow={3}
            textAlign="center"
          >
            A+
          </Text>
          <Text
            h="7"
            w="10"
            bg="white"
            rounded="md"
            shadow={3}
            textAlign="center"
          >
            A-
          </Text>
          <Text
            h="7"
            w="10"
            bg="white"
            rounded="md"
            shadow={3}
            textAlign="center"
          >
            B+
          </Text>
          <Text
            h="7"
            w="10"
            bg="white"
            rounded="md"
            shadow={3}
            textAlign="center"
          >
            B-
          </Text>
          <Text
            h="7"
            w="10"
            bg="white"
            rounded="md"
            shadow={3}
            textAlign="center"
          >
            O+
          </Text>
          <Text
            h="7"
            w="10"
            bg="white"
            rounded="md"
            shadow={3}
            textAlign="center"
          >
            O-
          </Text>
          <Text
            h="7"
            w="10"
            bg="white"
            rounded="md"
            shadow={3}
            textAlign="center"
          >
            AB+
          </Text>
          <Text
            h="7"
            w="10"
            bg="white"
            rounded="md"
            shadow={3}
            textAlign="center"
          >
            AB-
          </Text>
        </ScrollView>
      </HStack>
      <SearchBar setQuery={setQuery} />
      <ScrollView>
        {isUrgent ? filteredUrgent : donated ? filterDonated : requestList}
      </ScrollView>
    </View>
  );
};

export default observer(RequestsList);
