import { makeAutoObservable, runInAction } from "mobx";
import { instance } from "./instance";
import decode from "jwt-decode";
import AsyncStorage from "@react-native-async-storage/async-storage";

class AuthStore {
	user = null;

	constructor() {
		makeAutoObservable(this);
	}

	setUser = async (token) => {
		try {
			await AsyncStorage.setItem("myToken", token);
			this.user = decode(token);
			instance.defaults.headers.common.Authorization = `Bearer ${token}`;
		} catch (error) {
			console.log(
				"ðŸš€ ~ file: authStore.js ~ line 20 ~ AuthStore ~ setUser= ~ error",
				error
			);
		}
	};

	signup = async (userData, navigation, toast) => {
		try {
			const res = await instance.post("/signup", userData);
			this.setUser(res.data.token);
			navigation.replace("Tabs");
		} catch (error) {
			console.log("AuthStore -> signup -> error", error);
			toast.show({
				status: "error",
				title: "Invalid Login",
				description: "The username or password you entered is incorrect",
			});
		}
	};

	signin = async (user, navigation, toast) => {
		try {
			const res = await instance.post("/signin", user);
			this.setUser(res.data.token);
			navigation.replace("Tabs");
		} catch (error) {
			toast.show({
				status: "error",
				title: "Invalid Login",
				description: "The username or password you entered is incorrect",
			});
		}
	};

	signout = async () => {
		try {
			delete instance.defaults.headers.common.Authorization;
			await AsyncStorage.removeItem("myToken");
			runInAction(() => {
				this.user = null;
			});
		} catch (error) {
			console.log(
				"ðŸš€ ~ file: authStore.js ~ line 62 ~ AuthStore ~ signout= ~ error",
				error
			);
		}
	};

	checkForToken = async () => {
		try {
			const token = await AsyncStorage.getItem("myToken");
			if (token) {
				const user = decode(token);
				const userExp = user.exp * 1000;
				if (userExp > Date.now()) {
					this.setUser(token);
				} else {
					this.signout();
				}
			}
		} catch (error) {
			console.log(
				"ðŸš€ ~ file: authStore.js ~ line 82 ~ AuthStore ~ error",
				error
			);
		}
	};
	editUser = async (updatedProfile, toast) => {
		try {
			const formData = new FormData();
			for (const key in updatedProfile) {
				formData.append(key, updatedProfile[key]);
			}
			const res = await instance.put(`/${updatedProfile._id}`, formData);
			runInAction(() => {
				this.user = res.data;
			});

			toast.show({
				title: "Profile Updated",
				status: "success",
				placement: "top",
			});
		} catch (error) {
			console.error("profileStore --> updateProfile", error);
			toast.show({
				title: "Unauthorized",
				status: "error",
				description: "You can only edit your profile. ",
				placement: "top",
			});
		}
	};
}

const authStore = new AuthStore();
authStore.checkForToken();
export default authStore;
